<template>
  <div class="content">
    <my-header />
    <main>
      <slider :sliderConfig="$store.state.slider.images_header" :imageCount="1" />
      <div v-if="marquee.length > 0" class="marquee" ref="slider">
        <marquee-text :repeat="10">
          <span>{{ marquee }}</span>
        </marquee-text>
      </div>
      <div class="home-content">
        <RandomGoods :randGoods="random" />
      </div>
      <div class="main-banner">
        <img
            v-if="$store.state.display_width <= 1600"
            v-lazy="{src:'https://u1600792.isp.regruhosting.ru/img_server/sub_imgsm.webp', loading: 'data:image/gif;base64,'}"
            alt="slide"
        />
        <img
            v-else
            v-lazy="{src:'https://u1600792.isp.regruhosting.ru/img_server/sub_img.webp', loading: 'data:image/gif;base64,'}"
            alt="slide"
        />
      </div>
    </main>
    <my-footer />
  </div>
</template>

<script>
import Slider from "@/components/Slider";
import RandomGoods from "@/components/RandomGoods";
import MarqueeText from 'vue-marquee-text-component'

export default {
  name: 'Home',
  components: {
    RandomGoods,
    Slider,
    MarqueeText
  },
  data: () => ({
    marquee: ''
  }),
  mounted() {
    this.$store.commit('slider/SET_MAIN_SCROLL', false);
    window.addEventListener('scroll', () => {
      if (this.$refs.slider) {
        this.$store.commit('slider/SET_SLIDER_BOTTOM', this.$refs.slider.getBoundingClientRect().top);
        if (this.$store.state.slider.header_height > this.$store.state.slider.slider_bottom) {
          this.$store.commit('slider/SET_MAIN_SCROLL', true);
        } else {
          this.$store.commit('slider/SET_MAIN_SCROLL', false);
        }
      }
    });
  },
  computed: {
    random() {
      return [...this.$store.state.goods].filter(elem => {
        return elem.product_code.indexOf('_NAME_') === -1 && elem.product_code.indexOf('SERT') === -1;
      }).sort(() => Math.random() - 0.5).slice(0, 6);
    }
  }
}
</script>

